import React, { Fragment, useState } from "react";

const EditTodo = ({ todo }) => {
  const [description, setDescription] = useState(todo.url);
  /*changed todo.description to todo.url*/

  //edit description function

  const updateDescription = async e => {
    e.preventDefault();
    try {
      const body = { description }; //maybe change this?
      const response = await fetch( 
        /*changed todo.todo_id to todo.post_id*/
        `http://localhost:5000/todos/${todo.post_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        }
      );

      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-warning mt-3 ml-3 mb-3 mr-3"
        data-toggle="modal"
        data-target={`#id${todo.post_id}`}
        /*changed todo.todo_id to todo.post_id*/
      >
        {"  "}Edit{"  "}
      </button>

      {/* 
        id = id10
      */}
      <div
        class="modal"
        id={`id${todo.post_id}`}
        /*changed todo.todo_id to todo.post_id*/
        onClick={() => setDescription(todo.url)}
        /*changed todo.description to todo.url*/
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Edit Todo</h4>
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                onClick={() => setDescription(todo.url)}
                /*changed todo.description to todo.url*/
              >
                &times;
              </button>
            </div>

            <div class="modal-body">
              <input
                type="text"
                className="form-control"
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </div>

            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-warning"
                data-dismiss="modal"
                onClick={e => updateDescription(e)}
              >
                Edit
              </button>
              <button
                type="button"
                class="btn btn-danger"
                data-dismiss="modal"
                onClick={() => setDescription(todo.url)}
                /*changed todo.description to todo.url*/
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditTodo;
